div.script:
//

    var currentSearchContainerId;
    function searchboxProcessWatermark(textField, watermark) {
        if (textField.val() == watermark)
        {
            textField.val('');
        }
        currentSearchContainerId = 'searchbox';
    }

    function searchboxSetAutoComplete() {
        var url = 'http://i4.services.social.microsoft.com/search/Widgets/SearchBox.jss';
        var appId = '1005';
        var watermark = 'Search for samples' ;
        var source = Galleries.searchIntellisense.getUrl(url, appId, 'searchBox', 'searchbox_searchImg', watermark, '\x2fsite\x2fsearch');
        Galleries.utility.loadJavaScript(source);
    }

    $(function() {
        var waterMark = 'Search for samples';
        var textField = $(&quot;#searchbox input[id='searchBox']&quot;);
        textField.keydown(function(e) {
            if (e.which == 13) {
                searchboxProcessWatermark (textField, waterMark);
            }
        });

        var searchBtn = $(&quot;#searchbox_searchImg&quot;);
        if(searchBtn.length &gt; 0) {
            searchBtn.click(function(e) {
                searchboxProcessWatermark (textField, waterMark);
            });
        }

        var globalsearchBox = window.epx_searchBox;
        if(globalsearchBox &amp;&amp; globalsearchBox.prototype) {
            var searchCall = globalsearchBox.executeSearch;
            if(searchCall) {
                window.epx_searchBox.prototype.executeSearch = function() {
                    searchboxProcessWatermark(textField, waterMark);
                    searchCall.call(this);
                };
            }
        }

        searchboxSetAutoComplete();

    });


////
